function height2(m,n,d,h)
{
    let lessThanD = 0;
    for(let i = 0; i < 1; i++)
    {
        for(let j = 0; j < n; j++)
        {
            if(j==0)
            {
                let element = h[i][j];
                let case1 = element - h[i][j+1];

                //negate
                if(case1 < 0)
                {
                    case1 *= -1;
                }

                if(case1 < d)
                {
                    lessThanD++;
                }
            }
            else if(j == n-1)
            {
                let element = h[i][j];
                let case1 = element - h[i][j-1];

                //negate
                if(case1 < 0)
                {
                    case1 *= -1;
                }

                if(case1 < d)
                {
                    lessThanD++;
                }
            }
            else 
            {
                let element = h[i][j];
                let case1 = element - h[i][j-1];
                let case2 = element - h[i][j+1];

                //negate
                if (case1 < 0)
                {
                    case1 *= -1;
                }

                if(case2 < 0)
                {
                    case2 *= -1;
                }

                if(case1 < d || case2 < d)
                {
                    lessThanD++;
                }

            }

        }


    }

    return lessThanD;
}

